mapper.select:
SELECT COUNT(*) FROM notice_tb n LEFT JOIN member_tb m ON n.member_id = m.member_id
AND n.category = #{category}
AND (
OR n.title LIKE CONCAT('%', #{keyword}, '%')
OR n.content LIKE CONCAT('%', #{keyword}, '%')
)

SELECT n.notice_id, n.member_id, n.category, n.title, n.content, n.view_count, n.is_fixed, n.created_at, n.updated_at, m.name AS member_name FROM notice_tb n LEFT JOIN member_tb m ON n.member_id = m.member_id
AND n.category = #{category}
AND (
OR n.title LIKE CONCAT('%', #{keyword}, '%')
OR n.content LIKE CONCAT('%', #{keyword}, '%')
)
ORDER BY n.is_fixed DESC, n.created_at DESC LIMIT #{startRow}, #{pageSize}

mapper.insert:
INSERT INTO notice_tb (
    member_id, category, title, content, is_fixed
) VALUES (
    #{member_id}, #{category}, #{title}, #{content}, 
    IFNULL(#{is_fixed}, 0)
)

mapper.update:
UPDATE notice_tb
SET view_count = view_count + 1
WHERE notice_id = #{noticeId}

mapper.select:
SELECT COUNT(*)
FROM notice_tb
WHERE category = #{category}

SELECT n.notice_id, n.member_id, n.category, n.title, n.content, n.view_count, n.is_fixed, n.created_at, n.updated_at, m.name AS member_name FROM notice_tb n LEFT JOIN member_tb m ON n.member_id = m.member_id
(n.title LIKE CONCAT('%', #{keyword}, '%') OR n.content LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY n.is_fixed DESC, n.created_at DESC LIMIT #{startRow}, #{pageSize}
